		if ($this->isColumnModified(AnswerPeer::STUDY_ID)) $criteria->add(AnswerPeer::STUDY_ID, $this->study_id);
		if ($this->isColumnModified(AnswerPeer::ANSWER)) $criteria->add(AnswerPeer::ANSWER, $this->answer);
		if ($this->isColumnModified(AnswerPeer::CREATED_AT)) $criteria->add(AnswerPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(AnswerPeer::UPDATED_AT)) $criteria->add(AnswerPeer::UPDATED_AT, $this->updated_at);
		if ($this->isColumnModified(AnswerPeer::DELETED_AT)) $criteria->add(AnswerPeer::DELETED_AT, $this->deleted_at);
		if ($this->isColumnModified(AnswerPeer::CREATED_BY)) $criteria->add(AnswerPeer::CREATED_BY, $this->created_by);
		if ($this->isColumnModified(AnswerPeer::UPDATED_BY)) $criteria->add(AnswerPeer::UPDATED_BY, $this->updated_by);
		if ($this->isColumnModified(AnswerPeer::DELETED_BY)) $criteria->add(AnswerPeer::DELETED_BY, $this->deleted_by);

		return $criteria;
	}

