	protected function doValidate($columns = null)
	{
		if (!$this->alreadyInValidation) {
			$this->alreadyInValidation = true;
			$retval = null;

			$failureMap = array();


			// We call the validate method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aSearchBase !== null) {
				if (!$this->aSearchBase->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aSearchBase->getValidationFailures());
				}
			}

			if ($this->aSystematicReview !== null) {
				if (!$this->aSystematicReview->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aSystematicReview->getValidationFailures());
				}
			}

			if ($this->asfGuardUserRelatedByCastingVote !== null) {
				if (!$this->asfGuardUserRelatedByCastingVote->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->asfGuardUserRelatedByCastingVote->getValidationFailures());
				}
			}

			if ($this->asfGuardUserRelatedByCreatedBy !== null) {
				if (!$this->asfGuardUserRelatedByCreatedBy->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->asfGuardUserRelatedByCreatedBy->getValidationFailures());
				}
			}

			if ($this->asfGuardUserRelatedByUpdatedBy !== null) {
				if (!$this->asfGuardUserRelatedByUpdatedBy->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->asfGuardUserRelatedByUpdatedBy->getValidationFailures());
				}
			}

			if ($this->asfGuardUserRelatedByDeletedBy !== null) {
				if (!$this->asfGuardUserRelatedByDeletedBy->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->asfGuardUserRelatedByDeletedBy->getValidationFailures());
				}
			}


			if (($retval = StudyPeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}


				if ($this->collStudyUserCriterias !== null) {
					foreach ($this->collStudyUserCriterias as $referrerFK) {
						if (!$referrerFK->validate($columns)) {
							$failureMap = array_merge($failureMap, $referrerFK->getValidationFailures());
