		$criteria->addJoin(StudyPeer::BASE_ID, SearchBasePeer::ID, $join_behavior);

		$criteria->addJoin(StudyPeer::SYSTEMATIC_REVIEW_ID, SystematicReviewPeer::ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseStudyPeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = StudyPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = StudyPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = StudyPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				StudyPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined SearchBase rows

				$key2 = SearchBasePeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = SearchBasePeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = SearchBasePeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					SearchBasePeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Study) to the collection in $obj2 (SearchBase)
				$obj2->addStudy($obj1);

			} // if joined row is not null

				// Add objects for joined SystematicReview rows

				$key3 = SystematicReviewPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = SystematicReviewPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = SystematicReviewPeer::getOMClass(false);

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					SystematicReviewPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Study) to the collection in $obj3 (SystematicReview)
				$obj3->addStudy($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

